package org.puyallupfamilyhistorycenter.service.cache;

/**
 * This interface is intended to be used to allow iterations to be interrupted and resumed later,
 * especially in the case of pagination
 * 
 * @author tibbitts
 * @param <E> the type of element being iterated over
 */
public interface IterationStrategy<E> {
    /**
     * Given the current element in the iteration, return the next element or null
     * if the last element has been reached
     * @param current the last element returned by the iterator
     * @return the next element
     */
    E next(E current);
}
